import { useParams } from "react-router-dom";
import React, { useState } from "react";
import NavBar from "../components/layout/Navbar";
import ".css";

const Details = () => {

    return (
    <div>
     <NavBar />
        <div className="container">
          <img src="product_image.jpg" alt="Product Image" className="product-image" />
          <h1 className="product-title">Product Title</h1>
          <div className="product-details">
            <p>This is a brief description of the product. Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
          </div>
          <div className="comment-section">
            <h2>Comments</h2>
            <div className="comment">
              <p className="comment-author">John Doe</p>
              <p className="comment-text">This product is amazing!</p>
            </div>
            <div className="comment">
              <p className="comment-author">Jane Smith</p>
              <p className="comment-text">I have been using this product for a while now, and I love it!</p>
            </div>
            {/* Add more comments here */}
          </div>
        </div>

    </div>
  );
};

export default Details;
