import { useParams } from "react-router-dom";
import React, { useState } from "react";
import NavBar from "../components/layout/Navbar";
import "./details.css";

const Details = () => {
  const { userId } = useParams();

  const [comment1Likes, setComment1Likes] = useState(10);
  const [comment1Dislikes, setComment1Dislikes] = useState(2);
  const [likedComments, setLikedComments] = useState([]);
  const [dislikedComments, setDislikedComments] = useState([]);

  const handleComment1LikeClick = () => {
    if (likedComments.includes(userId)) {
      return;
    }
    if (comment1Likes > 0) {
      setComment1Likes(comment1Likes - 1);
    } else {
      setComment1Likes(comment1Likes + 1);
    }
    setLikedComments([...likedComments, userId]);
  };

  const handleComment1DislikeClick = () => {
    if (dislikedComments.includes(userId)) {
      return;
    }
    if (comment1Dislikes > 0) {
      setComment1Dislikes(comment1Dislikes - 1);
    } else {
      setComment1Dislikes(comment1Dislikes + 1);
    }
    setDislikedComments([...dislikedComments, userId]);
  };

  return (
    <div>
      <NavBar />
      <div className="blog-post">
        <img className="blog-image" src="blog-image.jpg" alt="Blog Image" />
        <h1 className="blog-title">Blog Title</h1>
        <p className="blog-details">Blog Details</p>
        <div className="like-dislike">
          <button className="like-button" onClick={handleComment1LikeClick}>
            Like
          </button>
          <span className="like-count">{comment1Likes}</span>
          <button
            className="dislike-button"
            onClick={handleComment1DislikeClick}
          >
            Dislike
          </button>
          <span className="dislike-count">{comment1Dislikes}</span>
        </div>
      </div>

      <div className="comment-section">
        <h2>Comments</h2>
        <div className="comment">
          <p className="comment-author">John Doe</p>
          <p className="comment-text">This product is amazing!</p>
          <button
            className="like-button"
            onClick={handleComment1LikeClick}
          >
            Like
          </button>
          <span className="like-count">{comment1Likes}</span>
          <button
            className="dislike-button"
            onClick={handleComment1DislikeClick}
          >
            Dislike
          </button>
          <span className="dislike-count">{comment1Dislikes}</span>
        </div>
        <div className="comment">
          <p className="comment-author">Jane Smith</p>
          <p className="comment-text">I have been using this product for a while now, and I love it!</p>
          <button
            className="like-button"
            onClick={handleComment1LikeClick}
          >
            Like
          </button>
          <span className="like-count">{comment1Likes}</span>
          <button
            className="dislike-button"
            onClick={handleComment1DislikeClick}
          >
            Dislike
          </button>
          <span className="dislike-count">{comment1Dislikes}</span>
        </div>
        {/* Add more comments here */}
      </div>
    </div>
  );
};

export default Details;
