import React, { useState } from "react";
import { FaThumbsUp, FaThumbsDown } from "react-icons/fa";
import { useParams } from "react-router-dom";
import "./home.css";

const Home = () => {
  const [likes, setLikes] = useState(0);
  const [dislikes, setDislikes] = useState(0);
  const [category, setCategory] = useState("all");

  const handleCategoryClick = (cat) => {
    setCategory(cat === "all"? "all" : cat);
  };

  const posts = [
    {
      title: "Blog Title 1",
      details: "Blog Details 1",
      image: "blog-image.jpg",
      category: "all",
      likes: 10,
      dislikes: 2,
    },
    {
      title: "Blog Title 2",
      details: "Blog Details 2",
      image: "blog-image.jpg",
      category: "chocolate",
      likes: 5,
      dislikes: 1,
    },
    {
      title: "Chocolate Blog Post",
      details: "Chocolate Blog Details",
      image: "blog-image.jpg",
      category: "chocolate",
      likes: 15,
      dislikes: 3,
    },
    // Add more posts here
  ];

  return (
    <div>
      <ul className="controls">
        {categories.map((cat, index) => (
          <li
            className={`buttons ${cat === category? "active" : ""}`}
            data-filter={cat}
            key={index}
            onClick={() => handleCategoryClick(cat)}
          >
            {cat}
          </li>
        ))}
      </ul>
      <div className="image-container">
        {posts.filter((post) => post.category === category).map((post, index) => (
          <div className="image" key={index}>
            <img src={post.image} alt="Blog Image" />
            <div className="title-details-container">
              <h2>{post.title}</h2>
              <p>{post.details}</p>
              <div className="like-dislike-container">
                <button onClick={() => setLikes(post.likes + 1)}>
                  <FaThumbsUp />
                  <span>{post.likes}</span>
                </button>
                <button onClick={() => setDislikes(post.dislikes + 1)}>
                  <FaThumbsDown />
                  <span>{post.dislikes}</span>
                </button>
              </div>
              <Link to={`/details/${post.id}`}>
                <button>More Details</button>
              </Link>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const categories = [
  "all",
  "chocolate",
  "ice-cream",
  "cake",
  "juice",
  "sandwich",
];

export default Home;
