import React, { useState } from "react";
import { FaThumbsUp, FaThumbsDown } from "react-icons/fa";
import NavBar from "../components/layout/Navbar";
import icecream from "../image/bg.avif";
import "./home.css";
import { Link } from "react-router-dom";


const Home = () => {
  const [likes, setLikes] = useState(0);
  const [dislikes, setDislikes] = useState(0);
  const [category, setCategory] = useState("all");

  const handleCategoryClick = (cat) => {
    setCategory(cat === "all"? "all" : cat);
  };

  return (
    <div>
      <NavBar />
      <div className="hero">
        <h1>Welcome to Our Blog</h1>
      </div>
      <div className="gallery">
        <ul className="controls">
          {categories.map((cat, index) => (
            <li
              className={`buttons ${cat === category? "active" : ""}`}
              data-filter={cat}
              key={index}
              onClick={() => handleCategoryClick(cat)}
            >
              {cat}
            </li>
          ))}
        </ul>
        <div className="image-container ">
          {posts.filter((post) => post.category === category).map((post, index) => (
            <div className="image all" key={index}>
              <a href={post.image} className=" ">
                <img src={post.image} alt />
              </a>
              <div className="title-details-container">
                <h2>{post.title}</h2>
                <p>{post.details}</p>
                <div className="like-dislike-container">
                    <FaThumbsUp onClick={() => setLikes(likes + 1)} />
                    <p>{likes}</p>
                    <FaThumbsDown onClick={() => setDislikes(dislikes + 1)} />
                    <p>{dislikes}</p>
                </div>
                <Link to={`/details/${post.id}`}>
                    <button>More Details</button>
                </Link>
                </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

const categories = [
  "all",
  "ice-cream",
  "chocolate",
  "cake",
  "juice",
  "sandwich",
];

const posts = [
  {
    title: "Blog Title 1",
    details: "Blog Details 1",
    image: icecream,
    category: "ice-cream"
  },
  {
    title: "Blog Title 2",
    details: "Blog Details 2",
    image: icecream,
    category: "chocolate"
  },
  {
    title: "Blog Title 2",
    details: "Blog Details 2",
    image: icecream,
    category: "chocolate"
  },
  {
    title: "Blog Title 2",
    details: "Blog Details 2",
    image: icecream,
    category: "chocolate"
  },
  // Add more posts here
];

export default Home;
