import React, { useState } from "react";
import { FaThumbsUp, FaThumbsDown } from "react-icons/fa";
import NavBar from "../components/layout/Navbar";
import icecream from "../image/bg.avif";
import "./home.css";
import { Link } from "react-router-dom";


const Home = () => {

  const categories = [
    "all",
    "ice-cream",
    "chocolate",
    "cake",
    "juice",
    "sandwich",
  ];
  
  const posts = [
    {
      title: "Blog Title 1",
      details: "Blog Details 1",
      image: icecream,
      category: "ice-cream"
    },
    {
      title: "Blog Title 2",
      details: "Blog Details 2",
      image: icecream,
      category: "chocolate"
    },
    {
      title: "Blog Title 2",
      details: "Blog Details 2",
      image: icecream,
      category: "chocolate"
    },
    {
      title: "Blog Title 2",
      details: "Blog Details 2",
      image: icecream,
      category: "chocolate"
    },
    {
      title: "Blog Title 2",
      details: "Blog Details 2",
      image: icecream,
      category: ""
    },
    // Add more posts here
  ];

  const [likes, setLikes] = useState(0);
  const [dislikes, setDislikes] = useState(0);
  const [category, setCategory] = useState("all");

  const handleCategoryClick = (cat) => {
    setCategory(cat === ""? "" : cat);
  };

  return (
    <div>
      <NavBar />
  <section id="hero" className="hero">
  <div className="info d-flex align-items-center">
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-lg-6 text-center">
          <h2 data-aos="fade-down">Welcome to <span>UpConstruction</span></h2>
          <p data-aos="fade-up">Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.</p>
          <a data-aos="fade-up" data-aos-delay={200} href="#get-started" className="btn-get-started">Get Started</a>
        </div>
      </div>
    </div>
  </div>
  <div id="hero-carousel" className="carousel slide" data-bs-ride="carousel" data-bs-interval={5000}>
    <div className="carousel-item active" style={{backgroundImage: 'url(assets/img/hero-carousel/hero-carousel-1.jpg)'}} />
    <div className="carousel-item" style={{backgroundImage: 'url(assets/img/hero-carousel/hero-carousel-2.jpg)'}} />
    <div className="carousel-item" style={{backgroundImage: `url(${icecream})`}} />
    <div className="carousel-item" style={{backgroundImage: `url(${icecream})`}} />
    <div className="carousel-item" style={{backgroundImage: `url(${icecream})`}} />
    <a className="carousel-control-prev" href="#hero-carousel" role="button" data-bs-slide="prev">
      <span className="carousel-control-prev-icon bi bi-chevron-left" aria-hidden="true" />
    </a>
    <a className="carousel-control-next" href="#hero-carousel" role="button" data-bs-slide="next">
      <span className="carousel-control-next-icon bi bi-chevron-right" aria-hidden="true" />
    </a>
  </div>
</section>

      <div className="gallery">
        <ul className="controls">
          {categories.map((cat, index) => (
          <li
          className={`buttons ${cat === category? "active" : ""}`}
          data-filter={cat}
          key={index}
          onClick={() => handleCategoryClick(cat)}
        >
          {cat}
        </li>
          ))}
        </ul>
        <div className="image-container ">
        {posts.filter((post) => post.category === "" || post.category === category).map((post, index) => (
            <div className="image all" key={index}>
              <a href={post.image} className=" ">
                <img src={post.image} alt />
              </a>
              <div className="title-details-container">
                <h2>{post.title}</h2>
                <p>{post.details}</p>
                <div className="like-dislike-container">
                    <FaThumbsUp onClick={() => setLikes(likes + 1)} />
                    <p>{likes}</p>
                    <FaThumbsDown onClick={() => setDislikes(dislikes + 1)} />
                    <p>{dislikes}</p>
                </div>
                <Link to={`/details/${post.id}`}>
                    <button>More Details</button>
                </Link>
                </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
