import React, { useState, useEffect } from "react";
import { FaThumbsUp, FaThumbsDown } from "react-icons/fa";
import NavBar from "../components/layout/Navbar";
import icecream from "../image/bg.avif";
import "./home.css";
import { Link } from "react-router-dom";

const Home = () => {
  const [likes, setLikes] = useState(0);
  const [dislikes, setDislikes] = useState(0);

  const filterImages = (category) => {
    const imageContainers = document.querySelectorAll(".image-container");
    imageContainers.forEach((container) => {
      if (container.dataset.filter === category) {
        container.style.display = "block";
      } else {
        container.style.display = "none";
      }
    });
  };

  useEffect(() => {
    filterImages("all");
  }, []);

  return (
    <div>
      <NavBar />
      <div className="hero">
        <h1>Welcome to Our Blog</h1>
      </div>
      <div className="gallery">
        <ul className="controls">
          {categories.map((category, index) => (
            <li
              className={`buttons ${index === 0? "active" : ""}`}
              data-filter={category}
              key={index}
              onClick={() => filterImages(category)}
            >
              {category}
            </li>
          ))}
        </ul>
        <div className="image-container ice-cream active">
          <div className="image">
            <a href={icecream} className=" ">
              <img src={icecream} alt />
            </a>
            <div className="title-details-container">
              <h2>Ice Cream Blog Title</h2>
              <p>Ice Cream Blog Details</p>
              <div className="like-dislike-container">
                <FaThumbsUp onClick={() => setLikes(likes + 1)} />
                <p>{likes}</p>
                <FaThumbsDown onClick={() => setDislikes(dislikes + 1)} />
                <p>{dislikes}</p>
              </div>
              <Link to={{ pathname: "/details", state: { blogData: blogData } }}>
                <button>More Details</button>
              </Link>
            </div>
          </div>
        </div>
        <div className="image-container chocolate">
          <div className="image">
            <a href={icecream} className=" ">
              <img src={icecream} alt />
            </a>
            <div className="title-details-container">
              <h2>Chocolate Blog Title</h2>
              <p>Chocolate Blog Details</p>
              <div className="like-dislike-container">
                <FaThumbsUp onClick={() => setLikes(likes + 1)} />
                <p>{likes}</p>
                <FaThumbsDown onClick={() => setDislikes(dislikes + 1)} />
                <p>{dislikes}</p>
              </div>
              <Link to={{ pathname: "/details", state: { blogData: blogData } }}>
                <button>More Details</button>
              </Link>
            </div>
          </div>
        </div>
        <div className="image-container cake">
          <div className="image">
            <a href={icecream} className=" ">
              <img src={icecream} alt />
            </a>
            <div className="title-details-container">
              <h2>Cake Blog Title</h2>
              <p>Cake Blog Details</p>
              <div className="like-dislike-container">
                <FaThumbsUp onClick={() => setLikes(likes + 1)} />
                <p>{likes}</p>
                <FaThumbsDown onClick={() => setDislikes(dislikes + 1)} />
                <p>{dislikes}</p>
              </div>
              <Link to={{ pathname: "/details", state: { blogData: blogData } }}>
                <button>More Details</button>
              </Link>
            </div>
          </div>
        </div>
        {/* Repeat the above code for the other images */}
      </div>
    </div>
  );
};

const categories = [
  "all",
  "ice-cream",
  "chocolate",
  "cake",
  "juice",
  "sandwich",
];

const blogData = {
  title: "Blog Title",
  details: "Blog Details",
  // Add other properties as needed
};

export default Home;
