import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { faThumbsDown } from '@fortawesome/free-solid-svg-icons';
import { useParams } from "react-router-dom";
import React, { useState, useEffect } from "react";
import NavBar from "../components/layout/Navbar";
import "./details.css";
import { Button, Input, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';

const Details = () => {
  const { userId } = useParams();

  const [blogTitle, setBlogTitle] = useState('');
  const [blogDetails, setBlogDetails] = useState('');
  const [blogImage, setBlogImage] = useState('');

  const [blog1Likes, setBlog1Likes] = useState(10);
  const [blog1Dislikes, setBlog1Dislikes] = useState(2);
  const [blog1LikedByUser, setBlog1LikedByUser] = useState(false);
  const [blog1DislikedByUser, setBlog1DislikedByUser] = useState(false);

  const [commentDetails, setCommentDetails] = useState([
    { author: 'John Doe', text: 'This product is amazing!' },
    { author: 'Jane Smith', text: 'I have been using this product for a while now, and I love it!' },
  ]);

  const [comment1Likes, setComment1Likes] = useState(10);
  const [comment1Dislikes, setComment1Dislikes] = useState(2);
  const [comment1LikedByUser, setComment1LikedByUser] = useState(false);
  const [comment1DislikedByUser, setComment1DislikedByUser] = useState(false);

  const [popupOpen, setPopupOpen] = useState(false);
  const [newComment, setNewComment] = useState('');

  const handleBlog1LikeClick = () => {
    if (blog1LikedByUser) {
      setBlog1Likes(blog1Likes - 1);
      setBlog1LikedByUser(false);
    } else {
      setBlog1Likes(blog1Likes + 1);
      setBlog1LikedByUser(true);
    }
  };

  const handleBlog1DislikeClick = () => {
    if (blog1DislikedByUser) {
      setBlog1Dislikes(blog1Dislikes - 1);
      setBlog1DislikedByUser(false);
    } else {
      setBlog1Dislikes(blog1Dislikes + 1);
      setBlog1DislikedByUser(true);
    }
  };

  const handleComment1LikeClick = () => {
    if (comment1LikedByUser) {
      setComment1Likes(comment1Likes - 1);
      setComment1LikedByUser(false);
     setCommentDetails(commentDetails.map((comment) => {
        if (comment.text === 'This product is amazing!') {
          return { ...comment, likes: comment.likes - 1 };
        }
        return comment;
      }));
    } else {
      setComment1Likes(comment1Likes + 1);
      setComment1LikedByUser(true);
      setCommentDetails(commentDetails.map((comment) => {
        if (comment.text === 'This product is amazing!') {
          return { ...comment, likes: comment.likes + 1 };
        }
        return comment;
      }));
    }
  };

  const handleComment1DislikeClick = () => {
    if (comment1DislikedByUser) {
      setComment1Dislikes(comment1Dislikes - 1);
      setComment1DislikedByUser(false);
      setCommentDetails(commentDetails.map((comment) => {
        if (comment.text === 'This product is amazing!') {
          return { ...comment, dislikes: comment.dislikes - 1 };
        }
        return comment;
      }));
    } else {
      setComment1Dislikes(comment1Dislikes + 1);
      setComment1DislikedByUser(true);
      setCommentDetails(commentDetails.map((comment) => {
        if (comment.text === 'This product is amazing!') {
          return { ...comment, dislikes: comment.dislikes + 1 };
        }
        return comment;
      }));
    }
  };

  const handleCommentSubmit = () => {
    setCommentDetails([...commentDetails, { author: 'You', text: newComment }]);
    setNewComment('');
    setPopupOpen(false);
  };

  useEffect(() => {
    setBlogTitle('This is the blog title');
    setBlogDetails('This is the blog details');
    setBlogImage('https://picsum.photos/id/1015/1000/600/');
  }, []);

  return (
    <div className="details-container">
      <NavBar />
      <div className="blog-post">
        <div className="details-header">
          <h1>{blogTitle}</h1>
          <div className="details-header-actions">
            <FontAwesomeIcon icon={faHeart} onClick={handleBlog1LikeClick} />
            <span>{blog1Likes}</span>
            <FontAwesomeIcon icon={faThumbsDown} onClick={handleBlog1DislikeClick} />
            <span>{blog1Dislikes}</span>
          </div>
        </div>
        <div className="details-image">
          <img src={blogImage} alt={blogTitle} />
        </div>
        <div className="details-body">
          <p>{blogDetails}</p>
        </div>
        <div className="details-comments">
          <h2>Comments</h2>
          {commentDetails.map((comment, index) => (
            <div key={index} className="details-comment">
              <div className="details-comment-header">
                <span>{comment.author}</span>
                <div className="details-comment-actions">
                  <FontAwesomeIcon icon={faHeart} onClick={handleComment1LikeClick} />
                  <span>{comment1Likes}</span>
                  <FontAwesomeIcon icon={faThumbsDown} onClick={handleComment1DislikeClick} />
                  <span>{comment1Dislikes}</span>
                </div>
              </div>
              <p>{comment.text}</p>
            </div>
          ))}
          <Modal isOpen={popupOpen} toggle={() => setPopupOpen(!popupOpen)}>
            <ModalHeader toggle={() => setPopupOpen(!popupOpen)}>Add a comment</ModalHeader>
            <ModalBody>
              <Input type="textarea" value={newComment} onChange={(e) => setNewComment(e.target.value)} />
            </ModalBody>
            <ModalFooter>
              <Button color="primary" onClick={handleCommentSubmit}>Submit</Button>{' '}
              <Button color="secondary" onClick={() => setPopupOpen(false)}>Cancel</Button>
            </ModalFooter>
          </Modal>
          <Button color="primary" onClick={() => setPopupOpen(true)}>Add comment</Button>
        </div>
      </div>
    </div>
  );
};

export default Details;
