import React from "react";
import NavBar from "../components/layout/Navbar";
import icecream from '../image/bg.avif'
import './home.css';


const Home = () => {
  return (
    <div>
      <NavBar />
      <div className="hero">
        <h1>Welcome to Our Blog</h1>
      </div>
      <div className="gallery">
        <ul className="controls">
          {categories.map((category, index) => (
            <li
              className={`buttons ${index === 0? "active" : ""}`}
              data-filter={category}
              key={index}
            >
              {category}
            </li>
          ))}
        </ul>
        <div className="image-container">
          
        <a href= {icecream} className="image ice-cream">
                <img src={icecream}  alt />
                
                </a>
         </div>   

          {/* {images.map((image, index) => (
            <a
              href={image.url}
              className={`image ${image.category}`}
              key={index}
            >
              <img src={require(`../image/${image.url}`).default} alt={image.category} />
              <div className="overlay">
                <button className="like-btn">Like</button>
                <button className="dislike-btn">Dislike</button>
              </div>
            </a>
          ))} */}

        
      </div>
    </div>
  );
};

const categories = ["all", "ice-cream", "chocolate", "cake", "juice", "sandwich"];

const images = [
  { url: "bg.avif", category: "ice-cream" },
  { url: "bg.avif", category: "ice-cream" },
  { url: "bg.avif", category: "ice-cream" },
  { url: "bg.avif", category: "chocolate" },
  { url: "bg.avif", category: "chocolate" },
  { url: "cake1.jpg", category: "cake" },
  { url: "cake2.jpg", category: "cake" },
  { url: "cake3.jpg", category: "cake" },
  { url: "cake4.jpg", category: "cake" },
  { url: "cake5.jpg", category: "cake" },
  { url: "juice1.jpg", category: "juice" },
  { url: "juice2.jpg", category: "juice" },
  { url: "juice3.jpg", category: "juice" },
  { url: "sandwich1.jpg", category: "sandwich" },
  { url: "sandwich2.jpg", category: "sandwich" },
  { url: "sandwich3.jpg", category: "sandwich" },
  { url: "sandwich4.jpg", category: "sandwich" },
];

export default Home;
