import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { faThumbsDown } from '@fortawesome/free-solid-svg-icons';
import { useParams } from "react-router-dom";
import React, { useState, useEffect } from "react";
import NavBar from "../components/layout/Navbar";
import "./details.css";

const Details = () => {
  const { userId } = useParams();

  const [blogTitle, setBlogTitle] = useState('');
  const [blogDetails, setBlogDetails] = useState('');
  const [blogImage, setBlogImage] = useState('');

  const [blog1Likes, setBlog1Likes] = useState(10);
  const [blog1Dislikes, setBlog1Dislikes] = useState(2);
  const [blog1LikedByUser, setBlog1LikedByUser] = useState(false);
  const [blog1DislikedByUser, setBlog1DislikedByUser] = useState(false);

  const [commentDetails, setCommentDetails] = useState([
    { author: 'John Doe', text: 'This product is amazing!' },
    { author: 'Jane Smith', text: 'I have been using this product for a while now, and I love it!' },
  ]);

  const [comment1Likes, setComment1Likes] = useState(10);
  const [comment1Dislikes, setComment1Dislikes] = useState(2);
  const [comment1LikedByUser, setComment1LikedByUser] = useState(false);
  const [comment1DislikedByUser, setComment1DislikedByUser] = useState(false);

  const handleBlog1LikeClick = () => {
    if (blog1LikedByUser) {
      setBlog1Likes(blog1Likes - 1);
      setBlog1LikedByUser(false);
    } else {
      setBlog1Likes(blog1Likes + 1);
      setBlog1LikedByUser(true);
    }
  };

  const handleBlog1DislikeClick = () => {
    if (blog1DislikedByUser) {
      setBlog1Dislikes(blog1Dislikes - 1);
      setBlog1DislikedByUser(false);
    } else {
      setBlog1Dislikes(blog1Dislikes + 1);
      setBlog1DislikedByUser(true);
    }
  };

  const handleCommentLikeClick = (index) => {
    if (commentDetails[index].likedByUser) {
      setCommentDetails(commentDetails.map((comment, i) => {
        if (i === index) {
          return {...comment, likes: comment.likes - 1, likedByUser: false, dislikedByUser: false };
        } else {
          return comment;
        }
      }));
    } else {
      setCommentDetails(commentDetails.map((comment, i) => {
        if (i === index) {
          return {...comment, likes: comment.likes + 1, likedByUser: true, dislikedByUser: false };
        } else {
          return comment;
        }
      }));
    }
  };

  const handleCommentDislikeClick = (index) => {
    if (commentDetails[index].dislikedByUser) {
      setCommentDetails(commentDetails.map((comment, i) => {
        if (i === index) {
          return {...comment, dislikes: comment.dislikes - 1, likedByUser: false, dislikedByUser: false };
        } else {
          return comment;
        }
      }));
    } else {
      setCommentDetails(commentDetails.map((comment, i) => {
        if (i === index) {
          return {...comment, dislikes: comment.dislikes + 1, likedByUser: false, dislikedByUser: true };
        } else {
          return comment;
        }
      }));
    }
  };
  useEffect(() => {
    const blogData = {
      "blog-details": [
        {
          "title": "Blog Title",
          "details": "Blog Details",
          "image": "blog-image.jpg"
        }
      ]
    };

    const blog = blogData["blog-details"][0];
    setBlogTitle(blog.title);
    setBlogDetails(blog.details);
    setBlogImage(blog.image);
  }, []);

  return (
    <div>
      <NavBar />
      <div className="blog-post">
      <h1 className="blog-title">{blogTitle}</h1>
        <img className="blog-image" src={blogImage} alt="Blog Image" />
      
        <div className="blog_like-dislike">
          <button className="like-button" onClick={handleBlog1LikeClick}>
            <FontAwesomeIcon icon={faHeart} className={blog1LikedByUser? 'active' : ''} />
            <span className="like-count">{blog1Likes}</span>
          </button>
      
          <button
            className="dislike-button"
            onClick={handleBlog1DislikeClick}
          >
            <FontAwesomeIcon icon={faThumbsDown} />
            <span className="dislike-count">{blog1Dislikes}</span>
          </button>
        
        </div>
        <p className="blog-details">{blogDetails}</p>
       
      </div>

      <div className="comment-section">
        <h2>Comments</h2>
        {commentDetails.map((comment, index) => (
          <div className="comment" key={index}>
            <p className="comment-author">{comment.author}</p>
            <p className="comment-text">{comment.text}</p>
            <div className="like-dislike">
              <button className="like-button" onClick={handleComment1LikeClick}>
                <FontAwesomeIcon icon={faHeart} className={comment1LikedByUser? 'active' : ''} />
                <span className="like-count">{comment1Likes}</span>
              </button>
       
              <button
                className="dislike-button"
                onClick={handleComment1DislikeClick}
              >
                <FontAwesomeIcon icon={faThumbsDown} />
                <span className="dislike-count">{comment1Dislikes}</span>
              </button>
          
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Details;
