import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import { faThumbsDown } from '@fortawesome/free-solid-svg-icons';
import { faComment } from '@fortawesome/free-solid-svg-icons';
import { useParams } from "react-router-dom";
import React, { useState, useEffect } from "react";
import NavBar from "../components/layout/Navbar";
import "./details.css";

const Details = () => {
  const { userId } = useParams();

  const [blogTitle, setBlogTitle] = useState('');
  const [blogDetails, setBlogDetails] = useState('');
  const [blogImage, setBlogImage] = useState('');

  const [blog1Likes, setBlog1Likes] = useState(10);
  const [blog1Dislikes, setBlog1Dislikes] = useState(2);
  const [blog1LikedByUser, setBlog1LikedByUser] = useState(false);
  const [blog1DislikedByUser, setBlog1DislikedByUser] = useState(false);

  const [commentDetails, setCommentDetails] = useState([
    { author: 'John Doe', text: 'This product is amazing!', likes: 10, dislikes: 2, likedByUser: false, dislikedByUser: false, showPopup: false },
    { author: 'Jane Smith', text: 'I have been using this product for a while now, and I love it!', likes: 15, dislikes: 3, likedByUser: false, dislikedByUser: false, showPopup: false },
  ]);

  const handleBlog1LikeClick = () => {
    if (blog1LikedByUser) {
      setBlog1Likes(blog1Likes - 1);
      setBlog1LikedByUser(false);
    } else {
      setBlog1Likes(blog1Likes + 1);
      setBlog1LikedByUser(true);
    }
  };

  const handleBlog1DislikeClick = () => {
    if (blog1DislikedByUser) {
      setBlog1Dislikes(blog1Dislikes - 1);
      setBlog1DislikedByUser(false);
    } else {
      setBlog1Dislikes(blog1Dislikes + 1);
      setBlog1DislikedByUser(true);
    }
  };

  const handleComment1LikeClick = (index) => {
    if (commentDetails[index].likedByUser) {
      setCommentDetails(commentDetails.map((comment, i) => {
        if (i === index) {
          return {...comment, likes: comment.likes - 1, likedByUser: false, dislikedByUser: false };
        }
        return comment;
      }));
    } else {
      setCommentDetails(commentDetails.map((comment, i) => {
        if (i === index) {
          return {...comment, likes: comment.likes + 1, likedByUser: true, dislikedByUser: false };
        }
        return comment;
      }));
    }
  };

  const handleComment1DislikeClick = (index) => {
    if (commentDetails[index].dislikedByUser) {
      setCommentDetails(commentDetails.map((comment, i) => {
        if (i === index) {
          return {...comment, dislikes: comment.dislikes - 1, likedByUser: false, dislikedByUser: false };
        }
        return comment;
      }));
    } else {
      setCommentDetails(commentDetails.map((comment, i) => {
        if (i === index) {
          return {...comment, dislikes: comment.dislikes + 1, likedByUser: false, dislikedByUser: true };
        }
        return comment;
      }));
    }
  };

  const handleCommentPopup = (index) => {
    setCommentDetails(commentDetails.map((comment, i) => {
      if (i === index) {
        return {...comment, showPopup:!comment.showPopup };
      }
      return comment;
    }));
  };

  useEffect(() => {
    const blogData = {
      "blog-details": [
        {
          "title": "Blog Title",
          "details": "Blog Details",
          "image": "blog-image.jpg"
        }
      ]
    };

    const blog = blogData["blog-details"][0];
    setBlogTitle(blog.title);
    setBlogDetails(blog.details);
    setBlogImage(blog.image);
  }, []);

  return (
    <div className="details-container">
      <NavBar />
      <div className="details-content">
        <div className="details-header">
          <h1>{blogTitle}</h1>
          <div className="details-header-icons">
            <FontAwesomeIcon icon={faHeart} onClick={handleBlog1LikeClick} />
            <span>{blog1Likes}</span>
            <FontAwesomeIcon icon={faThumbsDown} onClick={handleBlog1DislikeClick} />
            <span>{blog1Dislikes}</span>
            <FontAwesomeIcon icon={faComment} onClick={() => handleCommentPopup(0)} />
          </div>
        </div>
        <div className="details-image">
          <img src={blogImage} alt={blogTitle} />
        </div>
        <div className="details-body">
          <p>{blogDetails}</p>
        </div>
        <div className="details-comments">
          {commentDetails.map((comment, index) => (
            <div className="details-comment" key={index}>
              <div className="details-comment-header">
                <h4>{comment.author}</h4>
                <div className="details-comment-options">
                  <FontAwesomeIcon icon={faHeart} onClick={() => handleComment1LikeClick(index)} />
                  <span>{comment.likes}</span>
                  <FontAwesomeIcon icon={faThumbsDown} onClick={() => handleComment1DislikeClick(index)} />
                  <span>{comment.dislikes}</span>
                </div>
              </div>
              <div className="details-comment-body">
                <p>{comment.text}</p>
              </div>
              {comment.showPopup && (
                <div className="details-comment-popup">
                  <p>This is a popup for comment {index + 1}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Details;
